mapper.update:
UPDATE equipment
SET name = #{name}, type = #{type}, quantity = #{quantity}, status = #{status}
WHERE id = #{id}

UPDATE equipment
SET status = 'REPAIRING', repair_reason = #{reason}
WHERE id = #{id}

UPDATE equipment
SET status = CASE WHEN #{approved} THEN 'APPROVED' ELSE 'REJECTED' END
WHERE id = #{id}

UPDATE equipment
SET status = 'BORROWED', borrower = #{borrower}
WHERE id = #{id}

UPDATE equipment
SET status = 'AVAILABLE', borrower = NULL
WHERE id = #{id}

mapper.select:
SELECT * FROM equipment


        AND name LIKE CONCAT('%', #{name}, '%')


        AND type = #{type}


        AND status = #{status}
